          description: "" + isNull(listing.description),
          image: "" + missingImagesFiltered(listing.image),
          url: "" + isNull(listing.url),
          site: "facebook"
        }
      }
    )

    this.setState({
      facebookData : {
        data: facebookDataParsed,
        status: 'resolved'
      }
    });

