        array[j] = temp;
    }
    return array;
}

  this.state.resultsDisplay === "‚ö°Ô∏è Trending ‚ö°Ô∏è" ? this.setState(prevState => ({Data: shuffleArray(prevState.Data), loading: false}))
  : this.state.resultsDisplay === "Highest price first ‚ñ≤" ? this.setState(prevState => ({Data: prevState.Data.sort(highest), loading: false}))
  : this.state.resultsDisplay === "Lowest price first ‚ñº" ? this.setState(prevState => ({Data: prevState.Data.sort(lowest), loading: false}))
  : this.setState(prevState => ({Data: prevState.Data, loading: false}))

}

///>Data calls
////>Functions and constants

  ///<Preliminary checks
  if (!searchQuery) {
    alert("Tell me what to search! üòÅ");
    return;
  }

  console.time("complete search")
  this.setState({Data: [], searchTerm: searchQuery.toLowerCase(), loading: true, landing: false});
  if (Date.now() > this.state.ExpiryDate) {
    console.log("this app does not have an access token! Getting access token")
    seteBayAccessToken()
  } else {
    console.log("this has an access token" + this.state.AccessToken.substring(10,25));
  }
  ///>Preliminary checks

  ///<The Timeline
  setEbayData();
  setDepopData();
  setGumtreeData();
  setFacebookData();
  setEtsyData();
  setTrashNothingData();
  this.reOrderDisplay()



  }

  handleChange(searchQuery) {
    this.setState({ searchTerm: searchQuery });
    this.setData(searchQuery);

  }

  togglePopUp(listing) {
    if (this.state.popUpListing.display === true ) {
      this.setState({
        popUpListing: {
          display: false,
          listing: {}
        }
      })
    } else {
      this.setState({
        popUpListing: {
          display: true,
          listing: listing
        }
      })
  }
} 
changeActiveListing(listing, direction) {
    let currentActiveListingIndex = this.state.Data.indexOf(listing);

    if (direction === "next") {
      this.setState((prevState) => ({
        popUpListing: {
          display: true,
          listing: prevState.Data[currentActiveListingIndex + 1]
        }
      }))
    } else if (direction === "previous" && currentActiveListingIndex > 0) {
      this.setState((prevState) => ({
        popUpListing: {
          display: true,
          listing: prevState.Data[currentActiveListingIndex - 1]
        }
      }))
    } else {
      return;
    }


  }

  render() {
    return (
      <div className="App w-100 h-full bg-white overflow-x-hidden z-0">
          {this.state.popUpListing.display ? <PopUpListing changeActiveListing={this.changeActiveListing} display={this.state.popUpListing.display} listing={this.state.popUpListing.listing} togglePopUp={this.togglePopUp}/> : null}
          <SearchBar onChange={this.handleChange}/>


          {this.state.landing ? <Landing /> : null } 
          {this.state.loading ? null : <Results Data={this.state.Data}
                  resultsDisplay={this.state.resultsDisplay}
                  toggleResults={this.toggleDisplay}
                  searchTerm={this.state.searchTerm}
                  search={this.handleChange}
                  togglePopUp={this.togglePopUp}
                  />}

          {this.state.loading ? <LoadingPop /> : <p></p>}

      </div>
    );
  }
}

export default App;
